<div id="form-plugins">
    <div class="row">
        <div class="col-lg-6">
            <!--jQuery Knob-->
            <div class="panel panel-light">
                <div class="panel-heading">
                    <div class="panel-title">
                        jQuery Knob
                    </div>
                </div>
                <div class="panel-body no-padding">
                    <form action class="lobi-form">
                        <fieldset>
                            <header>Knob style input</header>
                            <div class="text-center">
                                <div class="margin-right-10 display-inline-block">
                                    <input class="knob" data-width="110" data-height="110" data-displayInput=false data-fgColor="#3989c6" value="35">
                                </div>
                                <div class="margin-right-10 display-inline-block">
                                    <input class="knob" data-width="110" data-height="110" data-cursor=true data-fgColor="#209e61" data-thickness=.3 value="29">
                                </div>
                                <div class="margin-right-10 display-inline-block">
                                    <input class="knob" data-width="110" data-height="110" data-min="-100" data-fgColor="#d6451d" data-displayPrevious=true value="44">
                                </div>
                                <div class="margin-right-10 display-inline-block">
                                    <input class="knob" data-width="110" data-height="110" data-fgColor="#e2993f" data-angleOffset=90 data-linecap=round value="35">
                                </div>
                            </div>
                        </fieldset>
                    </form>
                </div>
            </div>
            <!--noUISlider-->
            <div class="panel panel-light">
                <div class="panel-heading">
                    <div class="panel-title">
                        noUISlider
                    </div>
                </div>
                <div class="panel-body no-padding">
                    <form action class="lobi-form">
                        <fieldset>
                            <header>Using HTML5 input elements</header>
                            <div id="html5" class="margin-bottom-10"></div>
                            <select id="input-select"></select>
                            <input type="number" id="input-number"/>

                        </fieldset>
                        <fieldset>
                            <header>Non linear slider</header>
                            <div id="nonlinear" class="margin-bottom-15"></div>
                        </fieldset>
                        <fieldset>
                            <header>Locking sliders together</header>
                            <div class="slider margin-bottom-15" id="slider1"></div>
                            <div class="slider margin-bottom-15" id="slider2"></div>
                            <button id="sliderBtn" class="btn btn-default pull-right">Lock</button>
                        </fieldset>
                        <fieldset>
                            <header>Snapping between steps</header>
                            <div id="slider-snap"></div>
                        </fieldset>
                        <fieldset>
                            <header>Limit</header>
                            <div id="slider-limit"></div>
                        </fieldset>
                        <fieldset>
                            <header>Orientation</header>
                            <div id="slider-vertical" style="height: 150px;"></div>
                            <a href="http://refreshless.com/nouislider/" target="_blank" class="btn btn-default pull-right">Read more</a>
                        </fieldset>
                    </form>
                </div>
            </div>
            <!--ClockPicker-->
            <div class="panel panel-light">
                <div class="panel-heading">
                    <div class="panel-title">
                        <h4>ClockPicker</h4>
                    </div>
                </div>
                <div class="panel-body">
                    <div class="form-group">
                        <div class="input-group clockpicker" data-placement="top" data-align="right">
                            <input type="text" class="form-control" placeholder="Select time">
                            <span class="input-group-addon">
                                <span class="glyphicon glyphicon-time"></span>
                            </span>
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="control-label">Autoclose set to true</label>
                        <div class="input-group clockpicker" data-autoclose="true" data-placement="top">
                            <input type="text" class="form-control" placeholder="Select time">
                            <span class="input-group-addon">
                                <span class="glyphicon glyphicon-time"></span>
                            </span>
                        </div>
                    </div>
                </div>
            </div>
            <!--Bootstrap Timepicker-->
            <div class="panel panel-light">
