                            <span class="input-group-addon"><i class="fa fa-clock-o"></i></span>
                        </div>
                    </div>
                </div>
            </div>
            <!--Bootstrap MaxLength-->
            <div class="panel panel-light">
                <div class="panel-heading">
                    <div class="panel-title">
                        <h4>Bootstrap MaxLength</h4>
                    </div>
                </div>
                <div class="panel-body">
                    <div class="form-group">
                        <input type="text" class="form-control" maxlength="25" placeholder="Input with max length" name="defaultconfig" id="defaultconfig" />
                    </div>
                    <div class="form-group">
                        <input type="text" class="form-control" maxlength="25" placeholder="Input with max length" name="alloptions" id="alloptions" />
                    </div>
                    <div class="form-group">
