                            <span class="input-group-addon"><i class="fa fa-calendar"></i></span>
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="control-label">Month view</label>
                        <div id="datepicker-demo-month" class="input-group date">
                            <span class="input-group-addon"><i class="fa fa-calendar"></i></span>
                            <input type="text" class="form-control" placeholder="Please pick a month">
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="control-label">Auto close</label>
                        <div id="datepicker-demo-autoclose" class="input-group date">
                            <span class="input-group-addon"><i class="fa fa-calendar"></i></span>
                            <input type="text" class="form-control"  placeholder="Please pick a date">
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="control-label">Multiple dates</label>
                        <div id="datepicker-demo-multiple" class="input-group date">
                            <span class="input-group-addon"><i class="fa fa-calendar"></i></span>
                            <input type="text" class="form-control" placeholder="Please pick several dates">
                        </div>
                    </div>
                    <div class="form-group">
                        <label class="control-label">Range picker</label>
                        <div class="input-daterange input-group datepicker-demo">
                            <input type="text" class="form-control" name="start" placeholder="Start Date" />
                            <span class="input-group-addon">to</span>
                            <input type="text" class="form-control" name="end" placeholder="End date" />
                        </div>
                    </div>
                    <a href="https://github.com/eternicode/bootstrap-datepicker" target="_blank" class="btn btn-default pull-right" >Read more</a>
                </div>
            </div>
